import { Component } from "@angular/core";


@Component({

  selector: 'app-counter'
  , templateUrl: './counter.componet.html',

})
export class CounterComponet {
  public title: string = 'Counter Component';
  public counter: number = 0;

  increaseCounter(param: number): void {

    this.counter += param;

  }

  decreaseCounter(param: number): void {

    this.counter -= param;
    console.log(this.counter);
    console.log(param)
  }

  resetCounter(): void {

    this.counter = 0;

  }


}

<h2>
  {{title}}
</h2>
<h3>
  {{counter}}
</h3>

<button (click)="increaseCounter(1)">+1</button>
<button (click)="resetCounter()">Reset</button>
<button (click)="decreaseCounter(-1)">-1</button>
